import React, { useState, useEffect } from "react";

function Index(props) {
  const [curTime, setCurTime] = useState(new Date().toLocaleTimeString());
  const [localTime, setlocalTime] = useState(new Date().toLocaleTimeString());
  useEffect(() => {
    var timer = setInterval(
      () => setCurTime(new Date().toLocaleTimeString()),
      1000
    );
    return function cleanup() {
      clearInterval(timer);
    };
  });
  const setTime = () => {
    var time = curTime;
    var offSet = props.timezones.Hours._text;
    var [h, m, s] = time.split(":");
    var newTime = String(Math.abs(Number(h) + Number(offSet))) + `:${m}:${s}`;
    setlocalTime(newTime);
    console.log(newTime);
  };
  return (
    <div className="card">
      <div className="zone">
        <h1>{props.timezones.Name._text}</h1>
        <h3>GMT{props.timezones.Hours._text}</h3>
      </div>
      <div className="time">
        {/* <p>17:30:47</p> */}
        <p>{localTime}</p>
        <button onClick={setTime}>button</button>
      </div>
    </div>
  );
}

export default Index;
